import {Component, Input, OnInit} from '@angular/core';
import { CommonModule } from '@angular/common';
import {FormContainerComponent} from "../form/form-container.component";
import {ShortItemModel} from "../../core/models/shortItemModel";
import {Types} from "../../core/enums/TYPES";
import {BehaviorSubject} from "rxjs";

@Component({
  selector: 'empty-grid-item',
  standalone: true,
  imports: [CommonModule, FormContainerComponent],
  template: `
    <div class="grid__empty" (click)="onGridClick()">
      <form-container *ngIf="formIsVisible$ | async" [item]="emptyItem" [type]="type"></form-container>
      <div class="grid__empty__icon"></div>
    </div>
  `,
  styles: [`

  `]
})
export class EmptyGridItemComponent implements OnInit {
  @Input() type!: Types;
  formIsVisible$: BehaviorSubject<boolean> = new BehaviorSubject<boolean>(false);

  readonly emptyItem: ShortItemModel = {imagePath: "", name: "", price: 0};


  constructor() { }

  ngOnInit(): void {
  }

  onGridClick() {
    this.formIsVisible$.next(true);
  }
}
